class BoardItem{
  String title;
  String content;
  DateTime _writeDate;
  List<CommentItem> commentList;
  List<String> likeUsers= [];
  int get likeCount =>likeUsers.length;
  Applicant writer;
  String category;
  String _imageUrl;
  String imageUrl(int index){
    if(index == 0)
      return 'https://www.gettyimages.com/gi-resources/images/500px/983703508.jpg';
    else if(index == 1)
      return 'https://images.pexels.com/photos/2603464/pexels-photo-2603464.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=400';
    else if(index == 2)
      return 'https://static.scientificamerican.com/sciam/cache/file/BCC3BD1E-5DC0-4843-A841706AE575C694_source.jpg?w=590&h=800&39BBF62E-5F96-4C6A-A59590CCF416DA11';
    else if(index == 3)
      return 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQEDilDXtA7LcH1bemYYqueNavArQESiK9cJg&usqp=CAU';
    else if(index == 4)
      return 'https://breakinginthehabit.files.wordpress.com/2016/11/romance-box-1b75442dcb3e05ccbc614455b5ac2670.jpg';
    else if(index == 5)
      return 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcS7N_Pw6f8WEcIwPKTL-8BSjZJxBGIPy0HKXQ&usqp=CAU';
    else if(index == 6)
      return 'https://cdn.the-scientist.com/assets/articleNo/66864/aImg/35078/foresttb-m.jpg';
    else if(index == 7)
      return 'https://drscdn.500px.org/photo/100894035/m%3D900/v2?sig=cc38547b6eed7e69ecb5533ec5a2e25b779813062a5ef171256c13abc7dcfa46';
    else if(index == 8)
      return 'https://www.fcmtravel.com/sites/default/files/styles/grid_items/public/why-choose-summary.jpg?itok=LAqXcbBq';
    else if(index == 9)
      return 'data:image/jpeg;base64,';
    return 'https://www.gettyimages.com/gi-resources/images/500px/983703508.jpg';
  }
  String get writeDate =>DateFormat('yyyy. MM. dd. kk:mm').format(_writeDate);
  File file;
  BoardItem(this.title, this.content, this._writeDate, this.commentList, this.likeUsers,
      this.writer, this.category, this._imageUrl, this.file);

}

class CommentItem{
  String content;
  String get writeDate =>DateFormat('yyyy. MM. dd. kk:mm').format(_writeDate);
  DateTime _writeDate;
  List<CommentItem> commentList;
  int likeCount = 0;
  Applicant writer;
  CommentItem(this.content, this._writeDate, this.commentList, this.likeCount,
      this.writer);
}




